        private static Dictionary<string, Category>? categoryes;
        public static Dictionary<string, Category> Category
        {
            get
            {
                if (categoryes == null)
                {
                    var genratList = new Category[]
                    {
                             new Category{ CategoryName="Fruit pies", Description="All-fruity pies"},
                             new Category{ CategoryName="Cheese cakes", Description="Cheesy all the way"},
                             new Category{ CategoryName="Seasonal pies", Description="Get in the mood for a seasonal pie" }



                    };
                    categoryes = new Dictionary<string, Category>();
                    foreach (Category gen in genratList)
                    {
                        categoryes.Add(gen.CategoryName,gen);
                    }




                }
                return categoryes;
            }
        }
